import React, { useState } from 'react';
import AgregarContacto from './AgregarContacto';
import ListadoContacto from './ListadoContacto';
import './App.css';

function Padre() {
  const [contactos, setContactos] = useState([]);

  const agregarContacto = (contacto) => {
    fetch('http://www.raydelto.org/agenda.php', {
      method:'POST',
      body: JSON.stringify(contacto),
      headers: {
        'content-Type': 'application/json'
      }
    })
    .then(response => response.json())
    .then(data => {
      if (data.guardado) {
        setContactos([...contactos, contacto]);
      }else {
        alert ('Error')
      }
    });
  };
  return (
    <div className="Padre">
      <h1 className='h1-padre'>Agenda de contacto React</h1>
      <AgregarContacto onAgregar = {agregarContacto}  />
      <ListadoContacto/>
    </div>
  );
}

export default Padre;
