import React from 'react';
import AgregarContacto from './AgregarContacto';
import ListadoContacto from './ListadoContacto';
import './App.css';

function Padre() {
  const [contactos, setContactos] = useState([]);

  // Obtener los contactos guardados en localStorage
  const nombre = localStorage.getItem('nombre') || '';
  const email = localStorage.getItem('email') || '';
  const telefono = localStorage.getItem('telefono') || '';

  // Agregar un nuevo contacto a la lista
  const handleAgregarContacto = (nuevoContacto) => {
    setContactos([...contactos, nuevoContacto]);
  };

  return (
    <div className="App">
      <h1 className='h1-padre'>Agenda de contacto React</h1>
      <AgregarContacto/>
      <ListadoContacto contactos={contactos} />
    </div>
  );
}

export default Padre;
