import React from 'react';
import AgregarContacto from './AgregarContacto';
import ListadoContacto from './ListadoContacto';
import './App.css';

function Padre() {
  const [contactos, setContactos] = useState([]);

  const AgregarContacto = (nombre, email, telefono) =>{
    setContactos([...contactos, {nombre, email, telefono }]);
  };

  return (
    <div className="Padre">
      <h1 className='h1-padre'>agenda contactos react</h1>
       <AgregarContacto/>
       <ListadoContacto/>
    </div>
  );
}

export default Padre;
